if (context.global.currentTrip==null) {
	//create new trip object starting at this event
	context.global.currentTrip={_id:"trip:"+context.global.tripArray.length,
		startTime:parseInt(msg.payload.Time)};

	var dt = new Date(parseInt(msg.payload.Time));

		context.global.currentTrip.startHumanTime = {
			'month':	dt.getMonth() + 1,
			'day':		dt.getDate(),
			'year':		dt.getFullYear(), 
			'hours':	dt.getHours(),
			'mins':		dt.getMinutes(),
			'secs':		dt.getSeconds(),
			'msecs':	dt.getMilliseconds()
		}
	context.global.currentTrip.eventCount=1;
	context.global.currentTrip.endTime=msg.payload.Time;

	return null; //this event has been consumed starting the trip
	} else {

	//new event so add one to the count.
	context.global.currentTrip.eventCount=context.global.currentTrip.eventCount+1;
	//if this event is more than 100s after previous event, create new trip
	var firstTime, secondTime;
	firstTime=parseInt(context.global.currentTrip.endTime); //compating new event with current end time
	secondTime=parseInt(msg.payload.Time);
	var halfHour=1000*60*30;
	if (secondTime>(firstTime+halfHour)) {
		//start new Trip

		context.global.currentTrip.length=context.global.currentTrip.endTime-context.global.currentTrip.startTime;
		var msg={payload:context.global.currentTrip};
		var dt = new Date(parseInt(context.global.currentTrip.endTime));

		context.global.currentTrip.endHumanTime = {
			'month':	dt.getMonth() + 1,
			'day':		dt.getDate(),
			'year':		dt.getFullYear(),
			'hours':	dt.getHours(),
			'mins':		dt.getMinutes(),
			'secs':		dt.getSeconds(),
			'msecs':	dt.getMilliseconds()
		}



		context.global.tripArray.push(context.global.currentTrip);
		context.global.currentTrip=null;
		return msg;
		} else {

		//not new trip or closing trip, so save event in context.
		context.global.currentTrip.endTime=secondTime;

		}

	}




